mapper.select:
select DISTINCT project_id from task where section_id is not null

  and section_id = #{sectionQuery}

GROUP BY project_id

select a.id, a.name, a.demander, a.address, a.code, a.code_special, a.num, a.comment, a.status as projectStatus, b.firstCompleteStatus, b.secondCompleteStatus, b.thirdCompleteStatus, b.fourthCompleteStatus, b.firstStatus, b.secondStatus, b.thirdStatus, b.fourthStatus, c.name as sectionName, d.comp_status as signStatus, e.comp_status as debugStatus, r.rowNumber from project as a -- 行转列，求状态 left join ( select project_id, section_id, max(CASE WHEN task_name_id in (14,18,22,26) then comp_status end) as firstCompleteStatus, max(CASE WHEN task_name_id in (15,19,23,27) then comp_status end) as secondCompleteStatus, max(CASE WHEN task_name_id in (16,20,24,28) then comp_status end) as thirdCompleteStatus, max(CASE WHEN task_name_id in (17,21,25,29) then comp_status end) as fourthCompleteStatus, max(CASE WHEN task_name_id in (14,18,22,26) then status end) as firstStatus, max(CASE WHEN task_name_id in (15,19,23,27) then status end) as secondStatus, max(CASE WHEN task_name_id in (16,20,24,28) then status end) as thirdStatus, max(CASE WHEN task_name_id in (17,21,25,29) then status end) as fourthStatus from task where status = 1 group by project_id,section_id having section_id is not null ) as b on a.id = b.project_id left join section as c on b.section_id = c.id left join task d on a.id = d.project_id and d.task_name_id = 2 left join task e on a.id = e.project_id and e.task_name_id = 4 -- 求行号 left join ( select ROW_NUMBER() OVER(Order by x.order_no desc, x.code) as rowNumber, x.id from project as x where 1 = 1
and x.code like #{codeQuery}
and x.name like #{nameQuery}
and x.id in (${ids})
and year (x.create_time) = #{yearQuery}
and x.num = #{numQuery}
and x.demander like #{demanderQuery}
and x.address like #{addressQuery}
and x.create_time >= #{startTimeQuery}
and x.create_time <= #{endTimeQuery}
and x.design_task_status < 2 and x.status > 0
and x.design_task_status = 2 and x.status > 0
) as r on a.id = r.id where 1 = 1
and a.code like #{codeQuery}
and a.name like #{nameQuery}
and a.id in (${ids})
and a.design_task_status < 2 and a.status > 0
and a.design_task_status = 2 and a.status > 0
and year (a.create_time) = #{yearQuery}
and a.num = #{numQuery}
and a.demander like #{demanderQuery}
and a.address like #{addressQuery}
and a.create_time >= #{startTimeQuery}
and a.create_time <= #{endTimeQuery}
order by a.order_no desc , a.code
