mapper.select:
select a.id, a.name, a.demander, a.address, a.code, a.code_special, a.num, a.comment, a.status as projectStatus, b.comp_status as firstStatus, c.comp_status as secondStatus, d.comp_status as thirdStatus, e.comp_status as fourthStatus from project as a left join task as b on a.id = b.project_id and b.task_name_id = 10 left join task as c on a.id = c.project_id and c.task_name_id = 11 left join task as d on a.id = d.project_id and d.task_name_id = 12 left join task as e on a.id = e.project_id and e.task_name_id = 13 where 1 = 1
and a.code like #{codeQuery}
and a.name like #{nameQuery}
and year (a.create_time) = #{yearQuery}
and (b.comp_status != 2 or c.comp_status != 2 or d.comp_status != 2 or e.comp_status != 2) and a.status = 2
and b.comp_status = 2 and c.comp_status = 2 and d.comp_status = 2 and e.comp_status = 2 and a.status = 4
order by a.order_no desc , a.code
